mapper.select:
select count(*)
from carts
where user_id = #{userId}

select episode_id
from carts
where user_id = #{userId}

mapper.update:
update users
set sand = sand - #{totalPrice}
where id = #{userId}

mapper.delete:
delete from carts
where user_id=#{userId} and episode_id in

  #{episodeId}

mapper.select:
select sand
from users
where id = #{id}

mapper.update:
update users
set sand = sand + #{amount}
where id=#{userId}
